33.code:
#include&ltstdio.h&gt

int calcValue(int, int, int);

int main()
{
  printf("Result #1: %d\n", calcValue(5, 4, 3));
  printf("Result #2: %d\n", calcValue(3, 3, 4));

  return(0);
}

int calcValue(int x, int y, int z)
{
  int f1;
  int f2;
  int f3;

  f1 = x / y + x / z;
  f1 = 1 / f1 * x;

  f2 = y / x + y / z;
  f2 = f2 % 2 * y;

  f3 = 1 - (f2 + f1 + 2) % (f2 + f1 + 1);
  f3 *= z;

  return(f1 + f2 + f3);
}

