  return(0);
}

int makeTotal(int x, int opt)
{
  int i;
  int total = 0;
  for(i = x / 2; i >= 1; --i)
  {
    if(x % i == 0)
    {
      switch(opt)
      {
      case 1: total += i;
              break;
      case 2: total++;
      }
    }
  }
  return(total);
}

