#include&lt;stdio.h&gt;

int calcFactorial(int, int, int);

int main()
{
  printf("result: %d\n", calcFactorial(7, 2, 5));

  return(0);
}

int calcFactorial(int n, int a, int b)
{
  int fact = 1;

  if(n > 1)
  {
    if(n >= a && n <= b)
    {
      fact = n * calcFactorial(n - 1, a, b);
    }
    else
    {
      fact = calcFactorial(n - 1, a, b);
    }
  }
  return(fact);
}

