int findLocation(int[], int, int);

int main()
{
  int x[SETSIZE] = {3, 5, 2, 4, 9, 1, 8, 6, 5, 2};
  int i;
  int location;

  for(i = 1; i < SETSIZE; i++)
  {
    location = findLocation(x, i, x[i]);
    sortPass(x, i, location);
  }
  return(0);
}

void sortPass(int x[], int start, int end)
{
  int temp = x[start];
  while(start > end)
  {
    x[start] = x[start - 1];
    start--;
  }

  x[start] = temp;
}

int findLocation(int x[], int start, int element)
{
  while(start > 0 && element > x[start - 1])
  {
    start--;
  }

  return(start);
}

